mapper.select:
select name
from amembertable
where membercode = #{membercode}

select id 
from amembertable
where id = #{id}


select *
from amembertable
where id = #{id} and pw = #{pw}

mapper.insert:
 		insert into AMEMBERTABLE(
 			MEMBERCODE, 
	 		ID, 
	 		NAME, 
	 		PW, 
	 		HPNUMBER, 
	 		BIZNUMBER, 
	 		ADDRESS, 
	 		MAILADDRESS, 
	 		REGISTRATIONDATE, 
	 		TEMPLATECODE)
 		values 
 			(Amember_seq.NEXTVAL
 			, #{id}
 			, #{name}
 			, #{pw}
 			, #{hpnumber}
 			, #{biznumber}
 			, #{address}
 			, #{mailaddress}
 			, sysdate
 			, '0')
