mapper.select:

select * 
from Bcustomer 
where CUSTOMERID = #{customerid} 
	AND CUSTOMERPW = #{customerpw}
	AND membercode = #{membercode}

select count(*)
from Bcustomer
where customerid = #{customerid}

select *
from Bordertable
where membercode = #{membercode} AND customercode = #{customercode}

select count(*) 
from bcustomer 
where membercode = #{membercode}
		AND to_char(bpushdate, 'MM') = #{bpushdate}
